<?php class Megnor_Newproducts_IndexController extends Mage_Core_Controller_Front_Action {    public function indexAction() {	        if (!Mage::helper('newproducts')->getIsActive()) {            $this->_forward('noRoute');            return;        }        $template = Mage::getConfig()->getNode('global/page/layouts/' . Mage::getStoreConfig("newproducts/standalone/layout") . '/template');        $this->loadLayout();        $this->getLayout()->getBlock('root')->setTemplate($template);        $this->getLayout()->getBlock('head')->setTitle($this->__(Mage::getStoreConfig("newproducts/standalone/meta_title")));        $this->getLayout()->getBlock('head')->setDescription($this->__(Mage::getStoreConfig("newproducts/standalone/meta_description")));        $this->getLayout()->getBlock('head')->setKeywords($this->__(Mage::getStoreConfig("newproducts/standalone/meta_keywords")));        $breadcrumbsBlock = $this->getLayout()->getBlock('breadcrumbs');        $breadcrumbsBlock->addCrumb('new_products', array(            'label' => Mage::helper('newproducts')->__(Mage::helper('newproducts')->getPageLabel()),            'title' => Mage::helper('newproducts')->__(Mage::helper('newproducts')->getPageLabel()),        ));        $this->renderLayout();    }}